import React from 'react';
import './Controls.css';

function Controls({
  currentTurn,
  maxTurns,
  isPlaying,
  playbackSpeed,
  onPlayPause,
  onReset,
  onStepForward,
  onStepBackward,
  onSpeedChange,
  onTurnChange,
  availableGames,
  selectedGame,
  onGameSelect
}) {
  return (
    <div className="controls-container">
      <div className="game-selector">
        <label>Select Game:</label>
        <select
          value={selectedGame?.id || ''}
          onChange={(e) => {
            const game = availableGames.find(g => g.id === e.target.value);
            if (game) onGameSelect(game);
          }}
        >
          {availableGames.map(game => (
            <option key={game.id} value={game.id}>
              {game.name}
            </option>
          ))}
        </select>
      </div>

      <div className="playback-controls">
        <button onClick={onReset} title="Reset to start">
          ⏮ Reset
        </button>
        <button onClick={onStepBackward} disabled={currentTurn === 0} title="Step backward">
          ⏪ Step Back
        </button>
        <button onClick={onPlayPause} className="play-pause" title={isPlaying ? 'Pause' : 'Play'}>
          {isPlaying ? '⏸ Pause' : '▶ Play'}
        </button>
        <button onClick={onStepForward} disabled={currentTurn >= maxTurns - 1} title="Step forward">
          Step Forward ⏩
        </button>
      </div>

      <div className="turn-display">
        <span>Turn: {currentTurn + 1} / {maxTurns}</span>
        <input
          type="range"
          min="0"
          max={maxTurns - 1}
          value={currentTurn}
          onChange={(e) => onTurnChange(parseInt(e.target.value))}
          className="turn-slider"
        />
      </div>

      <div className="speed-control">
        <label>Speed:</label>
        <select value={playbackSpeed} onChange={(e) => onSpeedChange(parseInt(e.target.value))}>
          <option value={2000}>0.5x (2s)</option>
          <option value={1000}>1x (1s)</option>
          <option value={500}>2x (500ms)</option>
          <option value={250}>4x (250ms)</option>
          <option value={100}>10x (100ms)</option>
        </select>
      </div>
    </div>
  );
}

export default Controls;
